import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchFoodCategory, fetchFoodList, fetchFoodIngredientList } from '../redux/actions/foodActions';
import { FoodCard, FoodCategories, Header, Footer } from '../components';

class Foods extends Component {
  componentDidMount() {
    const { actionFetchFoodList, actionFetchCategories, ingredientQuery } = this.props;
    if (ingredientQuery === '') {
      actionFetchFoodList('');
    } else {

    }
      actionFetchCategories('list');
  }

  render() {
    const { foodCardsList } = this.props;
    return (
      <div>
        <Header title="Comidas" search />
        <FoodCategories />
        <ul>
          { foodCardsList.map((item, index) => (
            <FoodCard key={ item.idMeal } food={ item } index={ index } />)) }
        </ul>
        <Footer />
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  foodCardsList: state.foodReducer.foodCardsList,
  ingredientQuery: state.foodReducer.ingredientQuery,
});

const mapDispatchToProps = (dispatch) => ({
  actionFetchFoodList: (name) => dispatch(fetchFoodList(name)),
  actionFetchCategories: (category) => dispatch(fetchFoodCategory(category)),
  actionFetchIngredientFoodList:
    (ingredient) => dispatch(fetchFoodIngredientList(ingredient)),
});

Foods.propTypes = {
  actionFetchFoodList: PropTypes.func,
  actionFetchCategories: PropTypes.func,
}.isRequired;

export default connect(mapStateToProps, mapDispatchToProps)(Foods);
